// First query (retrieves buildings)
msg.topic = "INSERT INTO building(id,name,site) VALUES";
msg.payload.forEach(building => {
    msg.topic += "('" + building.id + "','" + building.name + "','" + building.site + "'),";
});
msg.topic = msg.topic.slice(0,-1) + ';'; // replacing the last character of the query
// Second query (retrieves floors)
msg.topic2 = "INSERT INTO floor(id,name,building,floor_number) VALUES";
msg.payload.forEach(building => {
    building.floors.forEach(floor => {
        msg.topic2 += "('" + floor.id + "','" + floor.name + "','" + floor.building + "','" + floor.floorNumber + "'),";
    });
});
msg.topic2 = msg.topic2.slice(0,-1) + ';'; // replacing the last character of the query
return msg;

msg.topic = "INSERT INTO asset(id,name,site,category,mac_address) VALUES";
msg.payload.forEach(asset => {
    msg.topic += "('" + asset.id + "','" + asset.name + "','" + asset.site + "','" + asset.assetcategory.name + "','" + asset.tag.macAddress + "'),";
});
msg.topic = msg.topic.slice(0,-1) + ';'; // replacing the last character of the query
return msg;

// set content of the answer in the flow to retrieve it later
msg.payload = {};
msg.payload.content=msg._dialogflow.fulfillmentText;
msg.payload.destJid=msg.destJid;
return msg;

flow.OAS_siteID = msg.OAS_siteID;
global.OAS_access_token = msg.OAS_access_token;
msg.topic = "SELECT id,mac_address,name FROM asset WHERE category='" + msg._dialogflow.parameters.fields.asset_type.stringValue + "'";
return msg;

msg.assets = msg.payload;
if (typeof msg.assets !="undefined" && msg.assets.length!==0){
    flow.OAS_siteID = msg.OAS_siteID;
    global.OAS_access_token = msg.OAS_access_token;
    // config for position getter
    msg.macAddress = "";
    msg.assets.forEach(asset => {
        msg.macAddress += asset.mac_address + ',';
    });
    msg.macAddress = msg.macAddress.slice(0,-1); // deleting last symbol
    msg.timeWindow = "60";
    return msg;
}

msg.topic = "INSERT INTO position(mac_address,floor_id,latitude,longitude) VALUES";
var noSolution = true;
// we prepare one sql request for each tag whose position can be found
msg.payload.inputs.macAddresses.forEach(tag => {
    // tag is the string value of each mac address
    // we only keep the most recent data
    var floor_id = "";
    var latitude = "";
    var longitude = "";
    var timestamp = 0;
    // we only take into account data whose position has been retrieved
    if (tag in msg.payload.metrics){
        // data is sorted by floor that has been visited recently so we compare the most recent timestamp of each floor to keep only the last one
        msg.payload.metrics[tag].forEach(floor => {
            var k = floor.data.length - 1; //k is the position in the array of the most recent timestamp for each floor
            if (floor.data[k].timestamp > timestamp) {
                floor_id = floor.floorId;
                latitude = floor.data[k].lat;
                longitude = floor.data[k].long;
                timestamp = floor.data[k].timestamp;
            }
        });
        msg.topic +=  "('" + tag + "','" + floor_id + "','" + latitude + "','" + longitude + "'),";
        noSolution=false;
    }
});
if (noSolution) {
    msg.topic="";
} else {
    msg.topic = msg.topic.slice(0,-1);
}
return msg;

if (msg.topic===""){
    msg.payload = {
        content: "Some assets corresponding to your request have been found, but we cannot access their locations. They are : ",
        destJid: msg.destJid
    };
    msg.assets.forEach(asset => {
        msg.payload.content += asset.name + ",";
    });
    msg.payload.content = msg.payload.content.slice(0,-1); // delete last symbol
    return msg;
}

var asset = msg.payload;
msg.payload.content = "The object is " + asset.asset + " and is located in " + asset.building_name + " - " + asset.floor_name + " (floor " + asset.floor_number + ") " + "at (" + asset.latitude + "," + asset.longitude + ")";
msg.payload.destJid = msg.destJid;
return msg;

var building = msg._dialogflow.parameters.fields.Building.stringValue;
var floor = msg._dialogflow.parameters.fields.Floor.stringValue;
var best_asset = msg.payload[0];
var sameFloor = false; // true if best_asset is at the same floor as the user
// we search for an asset at the same building and same floor
msg.payload.forEach(asset => {
    if (asset.building_name===building && ! sameFloor){
        best_asset = asset;
        if (asset.floor_name===floor){
            sameFloor=true;
        }
    }
});
msg.payload = best_asset;
return msg;
